import React, { useState } from 'react';
import Login from './components/Login.js';
import Chat from './components/Chat.js';
import Register from './components/Register.js';
//import UserSearch from '../UserSearch.js';
import './App.css';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState({user: null, logged: false});
  const [choice, setChoice] = useState(0); // 0 = login, 1 = register, 2 = chat
  //localStorage session
  localStorage.setItem('session', null);

  const handleLogin = (username, aukera) => {
    if (aukera === 0) {
      localStorage.setItem('session', username);
      alert(localStorage.getItem('session'));
      setChoice(2);
    } else {
      setChoice(1);
    }
  };

  const handleLogout = (auk) => {
    if (auk === 0) {
      localStorage.setItem('session', null);
      setChoice(0);
    } else {
      setChoice(2);
    }
  };

  const handleRegister = () => {
    setChoice(0);
  };

  return (
    <div className="app-container">
      {
        choice === 1 ? (
          <Register handleRegister={handleRegister} />
        ) : (choice === 2 && localStorage.getItem('session')) ? (
          <Chat handleLogout={handleLogout} loggedUser={localStorage.getItem('session')} />
        ) : (
          <Login handleLogin={handleLogin} />
        )
      }
    </div>
  );
};

export default App;


