//import logo from './logo.svg';
import './App.css';
import Chat from './component/Chat';
import Login from './component/Login';
import Register from './component/Register';
import React, {useState}  from 'react';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';

function App() {
  //const history = useHistory();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [choice, setChoice] = useState(0); // 0 = login, 1 = register, 2 = chat
  const [selectedUser, setSelectedUser] = useState(null);
  const [userList, setUserList] = useState([
    { id: 1, name: 'John', lastMessage: 'Hello there!' },
    { id: 2, name: 'Jane', lastMessage: 'How are you?' },
    { id: 3, name: 'Bob', lastMessage: 'Nice to see you!' },
    { id: 4, name: 'Alice', lastMessage: 'What are you up to?' },
  ]);
  const [searchQuery, setSearchQuery] = useState('');

  const handleLogin = (aukera) => {
    if (aukera == 0) {
      setIsLoggedIn(true);
      setChoice(2);
    } else {
      setIsLoggedIn(false);
      setChoice(1);
    }
  };

  return (
    <Router>
      <div>
        <h1>Chat App</h1>
        <nav>
          <ul>
            <li>
              <Link to="/">Inicio</Link>
            </li>
            <li>
              <Link to="/register">Register</Link>
            </li>
            <li>
              <Link to="/chat">Chat</Link>
            </li>
          </ul>
        </nav>
        <Switch>
          <Route exact path="/" Component={Login}/>
          <Route path="/register" Component={Register}/>
          <Route path="/chat" Component={Chat} />
        </Switch>
      </div>
    </Router>
    
  );
}

export default App;
