import React, { useState } from 'react';
import Login from './components/Login.js';
import Chat from './components/Chat.js';
import UserList from './components/UserList.js';
import Register from './components/Register.js';
//import UserSearch from '../UserSearch.js';
import './App.css';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [choice, setChoice] = useState(0); // 0 = login, 1 = register, 2 = chat
  const [selectedUser, setSelectedUser] = useState(null);
  const [userList, setUserList] = useState([
    { id: 1, name: 'John', lastMessage: 'Hello there!' },
    { id: 2, name: 'Jane', lastMessage: 'How are you?' },
    { id: 3, name: 'Bob', lastMessage: 'Nice to see you!' },
    { id: 4, name: 'Alice', lastMessage: 'What are you up to?' },
  ]);
  const [searchQuery, setSearchQuery] = useState('');

  const handleLogin = (aukera) => {
    if (aukera == 0) {
      setIsLoggedIn(true);
      setChoice(2);
    } else {
      setIsLoggedIn(false);
      setChoice(1);
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
  };

  const handleUserClick = (userId) => {
    setSelectedUser(userId);
  };

  const handleRegister = () => {
    setChoice(0);
  };

  const filteredUserList = userList.filter((user) =>
    user.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="app-container">
      {
        choice == 0 ? (
          <Login handleLogin={handleLogin} />
        ) : choice == 1 ? (
          <Register handleRegister={handleRegister} />
        ) : choice == 2 ? (
          <div className="chat-container">
          <div className="left-container">
            <UserList recentUsers={filteredUserList} handleUserClick={handleUserClick} />
          </div>
          <div className="right-container">
            <Chat selectedUser={selectedUser} handleLogout={handleLogout} />
          </div>
        </div>
        ) : (
          <Login handleLogin={handleLogin} />
        )
      }
    </div>
  );
};

export default App;


