import './App.css';
import Chat from './component/Chat';
import Login from './component/Login';
import Register from './component/Register';
import React  from 'react';
//import { Route, Switch, useHistory } from 'react-router';

function App() {
  //const history = useHistory();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [choice, setChoice] = useState(0); // 0 = login, 1 = register, 2 = chat
  const [selectedUser, setSelectedUser] = useState(null);
  const [userList, setUserList] = useState([
    { id: 1, name: 'John', lastMessage: 'Hello there!' },
    { id: 2, name: 'Jane', lastMessage: 'How are you?' },
    { id: 3, name: 'Bob', lastMessage: 'Nice to see you!' },
    { id: 4, name: 'Alice', lastMessage: 'What are you up to?' },
  ]);
  const [searchQuery, setSearchQuery] = useState('');

  const handleLogin = (aukera) => {
    if (aukera == 0) {
      setIsLoggedIn(true);
      setChoice(2);
    } else {
      setIsLoggedIn(false);
      setChoice(1);
    }
  };

  return (
    <>
      <div>
        <h1>Chat App</h1>
        < Register />
        < Login />
        < Chat />
      </div>
    </>
    
  );
}

export default App;
